import React, { Component } from 'react';
import { BrowserRouter as Router, Route} from 'react-router-dom';
import Header from './components/layouts/header';
import Technicians from './components/Technicians';
import AddTechnician from './components/AddTechnician';
import About from './components/pages/About';
import { v4 as uuidv4 } from 'uuid';
import './App.css';

class App extends Component {

  state = {
    technicians: [
      {
        id: uuidv4(),
        title: 'Take out the trash',
        completed: false,
      },
      {
        id: uuidv4(),
        title: 'Dinner with wife',
        completed: true,
      },
      {
        id: uuidv4(),
        title: 'Meeting with boss', 
        completed: false
      }
    ]
  }

  //Toggle Complete
  markComplete = (id) => {
  this.setState({ technicians: this.state.technicians.map(technician => {
    if(technician.id === id) {
      technician.completed = !technician.completed
    }
    return technician;
  }) });
}
  // Delete Technician
  delTechnician = (id) => {
    this.setState({ technicians: [...this.state.technicians.filter(technician => technician.id !== technician.id)] });
  }

  //Add Technician
  addTechnician = (title) => {
    const newTechnician = {
      id: uuidv4(),
      title,
      completed: false
    }
    this.setState({ technicians: [...this.state.technicians, newTechnician]})
  }

  render() {
    return (
      <Router>
        <div className="App">
          <div className="container">
            <Header />
            <Route exact path="/" render={props => (
              <React.Fragment>
                <AddTechnician addTechnician={this.addTechnician} />
                <Technicians technicians={this.state.technicians} markComplete={this.markComplete} delTechnician={this.delTechnician}/>
              </React.Fragment>
            )} />
            <Route path="/about" component={About} />
          </div>
        </div>
      </Router>
    );
  }
}

export default App;
